/* View: VIEW_ITEM_MOVIMENTO */
CREATE VIEW VIEW_ITEM_MOVIMENTO(
    ITEM_ID,
    EMPRESA_ID,
    PESSOA_ID,
    USER_ID,
    MOVIMENTO,
    QTDE,
    DESCRICAO,
    TIPO)
AS
SELECT
    X.ITEM_ID,
    X.EMPRESA_ID,
    X.PESSOA_ID,
    X.USER_ID,
    X.MOVIMENTO,
    X.QTDE,
    X.DESCRICAO,
    X.TIPO
FROM
(
SELECT
    VI.ITEM_ID,
    VI.EMPRESA_ID,
    V.PESSOA_ID,
    V.USER_ID,
