    0 AS TIPO
FROM VENDA_ITENS VI
JOIN VENDAS V ON(V.ID = VI.VENDA_ID AND V.EMPRESA_ID = VI.EMPRESA_ID)
WHERE V.SITUACAO = 'F'

UNION ALL

SELECT
    VI.ITEM_ID,
    VI.EMPRESA_ID,
    V.PESSOA_ID,
    V.USER_ID,
    VI.DELETED_AT AS MOVIMENTO,
    VI.QTDE,
    'CANCELAMENTO DA VENDA ' || V.REFERENCIA AS DESCRICAO,
    1 AS TIPO
FROM VENDA_ITENS VI
JOIN VENDAS V ON(V.ID = VI.VENDA_ID AND V.EMPRESA_ID = VI.EMPRESA_ID)
WHERE V.SITUACAO = 'F'
AND V.DELETED_AT IS NOT NULL

UNION ALL

SELECT
    CI.ITEM_ID,
    CI.EMPRESA_ID,
    C.PESSOA_ID,
    C.USER_ID,
    CI.CREATED_AT AS MOVIMENTO,
    CI.QTDE,
