    1 AS TIPO
FROM COMPRA_ITENS CI
JOIN COMPRAS C ON(C.ID = CI.COMPRA_ID AND C.EMPRESA_ID = CI.EMPRESA_ID)
WHERE C.SITUACAO = 'F'

UNION ALL

SELECT
    CI.ITEM_ID,
    CI.EMPRESA_ID,
    C.PESSOA_ID,
    C.USER_ID,
    CI.DELETED_AT AS MOVIMENTO,
    CI.QTDE,
