FROM COMPRA_ITENS CI
JOIN COMPRAS C ON(C.ID = CI.COMPRA_ID AND C.EMPRESA_ID = CI.EMPRESA_ID)
WHERE C.SITUACAO = 'F'
AND C.DELETED_AT IS NOT NULL

UNION ALL

SELECT
