FROM ITEM_INVENTARIOS IV
) X
;



/* View: VIEW_SPED_0150 */
CREATE VIEW VIEW_SPED_0150(
    EMPRESA_ID,
    PESSOA_ID,
    REFERENTE)
AS
SELECT
    X.EMPRESA_ID,
    X.PESSOA_ID,
    X.REFERENTE
FROM (
SELECT
    DISTINCT
    P.EMPRESA_ID,
    P.ID AS PESSOA_ID,
    CAST(LPAD(EXTRACT(YEAR FROM IIF(N.MODELO IN('04','55'), N.DSAIENT, N.DEMI)), 4, '0') AS VARCHAR(4)) ||
    CAST(LPAD(EXTRACT(MONTH FROM IIF(N.MODELO IN('04','55'), N.DSAIENT, N.DEMI)), 2, '0') AS VARCHAR(2)) AS REFERENTE
FROM NFES N
JOIN PESSOAS P ON(P.ID = N.PARTICIPANTE_ID AND P.EMPRESA_ID = P.EMPRESA_ID)
WHERE (((N.MODELO = '55') AND (N.CSTAT IN(100,150))) OR (N.MODELO = '04'))
AND ((N.TPNF_WT = 1) OR ((N.TPNF_WT = 0) AND (N.NFERECEBIDA = 4)))
) X
ORDER BY X.REFERENTE
;



/* View: VIEW_SPED_0190 */
