    U.ID AS UNIDADE_ID,
    CAST(LPAD(EXTRACT(YEAR FROM IIF(N.MODELO IN('04','55'), N.DSAIENT, N.DEMI)), 4, '0') AS VARCHAR(4)) ||
    CAST(LPAD(EXTRACT(MONTH FROM IIF(N.MODELO IN('04','55'), N.DSAIENT, N.DEMI)), 2, '0') AS VARCHAR(2)) AS REFERENTE
FROM NFE_DET ND
JOIN NFES N ON(N.ID = ND.NFE_ID AND N.EMPRESA_ID = ND.EMPRESA_ID)
JOIN ITENS I ON(I.ID = ND.ITEM_ID AND I.EMPRESA_ID = N.EMPRESA_ID)
JOIN UNIDADES U ON(U.ID = I.UNIDADE_ID AND U.EMPRESA_ID = I.EMPRESA_ID)
WHERE (((N.TPNF_WT = 0) AND (N.NFERECEBIDA = 4) AND (N.CSTAT IN(100,150))) OR ((N.TPNF_WT = 1) AND (N.MODELO = '04')))
) X
ORDER BY X.REFERENTE
;



