FROM NFES N
WHERE (((N.TPNF_WT = 1) AND (N.CSTAT IN(100,101,102,150,151))) OR ((N.TPNF_WT = 1) AND (N.MODELO = '04')) OR ((N.TPNF_WT = 0) AND (N.NFERECEBIDA = 4)))
) X
ORDER BY X.REFERENTE
;



/* View: VIEW_SPED_C190 */
CREATE VIEW VIEW_SPED_C190(
    EMPRESA_ID,
    NFE_ID,
    CSOSN,
    CST,
    CFOP,
    PICMS,
    VL_OPR,
    VL_BC_ICMS,
    VL_ICMS,
    VL_BC_ICMS_ST,
    VL_ICMS_ST,
    VL_IPI)
AS
SELECT
  X.EMPRESA_ID,
  X.NFE_ID,
  X.CSOSN,
  X.CST,
  X.CFOP,
  X.PICMS,
  SUM(X.VL_OPR) AS VL_OPR,
  SUM(X.VBC) AS VL_BC_ICMS,
  SUM(X.VICMS) AS VL_ICMS,
  SUM(X.VBCST) AS VL_BC_ICMS_ST,
  SUM(X.VICMSST) AS VL_ICMS_ST,
  SUM(X.VIPI) AS VL_IPI
FROM
(
SELECT
    ND.EMPRESA_ID,
    ND.NFE_ID AS NFE_ID,
    ICMS.CSOSN,
    ICMS.CST,
    ND.CFOP,
    ICMS.PICMS,
    (ND.VPROD + ND.VFRETE + ND.VSEG - ND.VDESC + ND.VOUTRO + ICMS.VICMSST + IPI.VIPI) AS VL_OPR,
    ICMS.VBC,
    ICMS.VICMS,
    ICMS.VBCST,
    ICMS.VICMSST,
    IPI.VIPI
FROM NFE_DET ND
JOIN EMPRESAS E ON(E.ID = ND.EMPRESA_ID)
JOIN NFE_DET_ICMS ICMS ON(ICMS.NFE_DET_ID = ND.ID AND ICMS.EMPRESA_ID = ND.EMPRESA_ID)
JOIN NFE_DET_IPI IPI ON(IPI.NFE_DET_ID = ND.ID AND IPI.EMPRESA_ID = ND.EMPRESA_ID)
JOIN NFES N ON(N.ID = ND.NFE_ID AND N.EMPRESA_ID = ND.EMPRESA_ID)
WHERE (((N.TPNF_WT = 1) AND (N.CSTAT IN(100,150))) OR ((N.TPNF_WT = 1) AND (N.MODELO = '04')) OR ((N.TPNF_WT = 0) AND (N.NFERECEBIDA = 4)))
) X
GROUP BY
    X.EMPRESA_ID,
    X.NFE_ID,
    X.CSOSN,
    X.CST,
    X.CFOP,
    X.PICMS
;



/* View: VIEW_TERMINAIS */
CREATE VIEW VIEW_TERMINAIS(
    ID,
    EMPRESA_ID,
    AUTHENTICATION,
    REFERENCIA,
    CONTA_ID,
    CATEGORIA_ID,
    PESSOA_ID,
    PARCELAMENTO,
    PRINT_PATH,
    NFCE_OPERACAO_FISCAL_ID,
    NFCE_NATOP,
    NFCE_SERIE,
    NFCE_NUMERO,
    NFCE_PRINT_PATH,
    NFCE_PRINTER_MODEL,
    MOVIMENTO_REFERENCIA,
    VENDA_REFERENCIA,
    RECEBIMENTO_REFERENCIA)
AS
SELECT T.ID,
       T.EMPRESA_ID,
       T.AUTHENTICATION,
       T.REFERENCIA,
       T.CONTA_ID,
       T.CATEGORIA_ID,
       T.PESSOA_ID,
       T.PARCELAMENTO,
       T.PRINT_PATH,
       T.NFCE_OPERACAO_FISCAL_ID,
       T.NFCE_NATOP,
       T.NFCE_SERIE,
       T.NFCE_NUMERO,
       T.NFCE_PRINT_PATH,
       T.NFCE_PRINTER_MODEL,

  (SELECT COALESCE(MAX(M.REFERENCIA),0)
   FROM MOVIMENTOS M
   WHERE (M.EMPRESA_ID = T.EMPRESA_ID)
     AND (M.TERMINAL_ID = T.ID)) AS MOVIMENTO_REFERENCIA,

  (SELECT COALESCE(MAX(V.REFERENCIA),0)
   FROM VENDAS V
   JOIN MOVIMENTOS M ON(M.ID = V.MOVIMENTO_ID
                        AND M.EMPRESA_ID = V.EMPRESA_ID)
   WHERE (M.EMPRESA_ID = T.EMPRESA_ID)
     AND (M.TERMINAL_ID = T.ID)) VENDA_REFERENCIA,

  (SELECT COALESCE(MAX(R.REFERENCIA),0)
   FROM RECEBIMENTOS R
   JOIN VENDAS V ON(V.ID = R.VENDA_ID
                    AND V.EMPRESA_ID = R.EMPRESA_ID)
   JOIN MOVIMENTOS M ON(M.ID = V.MOVIMENTO_ID
                        AND M.EMPRESA_ID = V.EMPRESA_ID)
   WHERE (M.EMPRESA_ID = T.EMPRESA_ID)
     AND (M.TERMINAL_ID = T.ID)) RECEBIMENTO_REFERENCIA
FROM TERMINAIS T
;



/* View: VIEW_TERMINAL_ITENS */
CREATE VIEW VIEW_TERMINAL_ITENS(
    ID,
    EMPRESA_ID,
    REFERENCIA,
    EAN,
    NOME,
    NCM,
    EXTIPI,
    UNIDADE,
    EAN_TRIBUTAVEL,
    UNIDADE_TRIBUTAVEL,
    CEST,
    PRECO_VENDA,
    CFOP,
    ICMS_ORIG,
    ICMS_CST,
    ICMS_CSOSN,
    IPI_CST,
    PIS_CST,
    COFINS_CST)
AS
SELECT I.ID,
       I.EMPRESA_ID,
       I.REFERENCIA,
       I.EAN,
       I.NOME,
       I.NCM,
       I.EXTIPI,
       U.UNIDADE AS UNIDADE,
       I.EAN_TRIBUTAVEL,
       UT.UNIDADE AS UNIDADE_TRIBUTAVEL,
       I.CEST,
       I.PRECO_VENDA,
       ICMS.CFOP,
       ICMS.ORIG AS ICMS_ORIG,
       ICMS.CST AS ICMS_CST,
       ICMS.CSOSN AS ICMS_CSOSN,
       IPI.CST AS IPI_CST,
       PIS.CST AS PIS_CST,
       COFINS.CST AS COFINS_CST
FROM ITENS I
JOIN EMPRESAS E ON(E.ID = I.EMPRESA_ID)
JOIN UNIDADES U ON(U.ID = I.UNIDADE_ID
                   AND U.EMPRESA_ID = I.EMPRESA_ID)
JOIN UNIDADES UT ON(UT.ID = I.UNIDADE_TRIBUTAVEL_ID
                    AND UT.EMPRESA_ID = I.EMPRESA_ID)
JOIN EMPRESA_CONFIGURACOES EC ON(EC.EMPRESA_ID = E.ID)
JOIN VIEW_CF_ICMS ICMS ON(ICMS.EMPRESA_ID = E.ID
                          AND ICMS.OPERACAO_FISCAL_ID = EC.NFCE_OPERACAO_FISCAL_ID
                          AND ICMS.GRUPO_TRIBUTARIO_ID = I.GRUPO_TRIBUTARIO_ID
                          AND ICMS.UF_DESTINO = E.UF)
JOIN VIEW_CF_IPI IPI ON(IPI.EMPRESA_ID = E.ID
                        AND IPI.OPERACAO_FISCAL_ID = EC.NFCE_OPERACAO_FISCAL_ID
                        AND IPI.GRUPO_TRIBUTARIO_ID = I.GRUPO_TRIBUTARIO_ID)
JOIN VIEW_CF_PIS PIS ON(PIS.EMPRESA_ID = E.ID
                        AND PIS.OPERACAO_FISCAL_ID = EC.NFCE_OPERACAO_FISCAL_ID
                        AND PIS.GRUPO_TRIBUTARIO_ID = I.GRUPO_TRIBUTARIO_ID)
JOIN VIEW_CF_COFINS COFINS ON(COFINS.EMPRESA_ID = E.ID
                              AND COFINS.OPERACAO_FISCAL_ID = EC.NFCE_OPERACAO_FISCAL_ID
                              AND COFINS.GRUPO_TRIBUTARIO_ID = I.GRUPO_TRIBUTARIO_ID)
;




